mapper.select:

SELECT member_id, 
	   product_id
FROM CART
WHERE member_id = #{memberId}
AND product_id = #{productId}

mapper.update:

UPDATE CART
SET cart_count = cart_count + 1
WHERE 
	member_id = #{memberId}
AND product_id = #{productId}

mapper.delete:
DELETE FROM CART
WHERE member_id = #{memberId}
AND product_id = #{productId}

mapper.select:
SELECT COUNT(product_id)
FROM CART
WHERE member_id=#{memberId}
