mapper.select:

SELECT image_original_name
FROM CENTER_IMAGE
WHERE notice_id = #{condition}


SELECT image_original_name
FROM CENTER_IMAGE
WHERE helpdesk_id = #{condition}

mapper.delete:

DELETE FROM CENTER_IMAGE
WHERE
	image_original_name = #{imageOriginalName}
AND notice_id = #{boardId}


DELETE FROM CENTER_IMAGE
WHERE
	image_original_name = #{imageOriginalName}
AND helpdesk_id = #{boardId}
